import React, { Component } from 'react';
import NewsItem from './NewsItem';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }

  async componentDidMount() {
    let url =
      'https://newsapi.org/v2/top-headlines?country=in&apiKey=4164cc6faad2423fa1df78508369a629';
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }
  render() {
    return (
      <>
        <div className='container my-3'>
          <h1 className='text-center my-4'>News Monkey - Top Headlines</h1>

          <div className='row'>
            {this.state.articles.map((myElement) => {
              return (
                <div className='col-md-4' key={myElement.url}>
                  <NewsItem
                    title={myElement.title ? myElement.title.slice(0, 45) : ''}
                    description={
                      myElement.description
                        ? myElement.description.slice(0, 88)
                        : ''
                    }
                    imageUrl={
                      myElement.urlToImage
                        ? myElement.urlToImage
                        : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                    }
                    newsUrl={myElement.url}
                  />
                </div>
              );
            })}
          </div>
        </div>
        <div className='container d-flex justify-content-between'>
          <button type='button' className='btn btn-dark'>
            &larr PREVIOUS
          </button>
          <button type='button' className='btn btn-dark'>
            NEXT &larr
          </button>
        </div>
      </>
    );
  }
}

export default News;
