import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';

export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 8,
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };

    const capitalFirstLetter = (string) => {
      return (
        string.slice(0, 1).toUpperCase() +
        string.slice(1, string.length).toLowerCase()
      );
    };

    document.title = `${capitalFirstLetter(this.props.category)} - NewsApp`;
  }

  async componentDidMount() {
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=4164cc6faad2423fa1df78508369a629&page=1&pageSize=${this.props.pageSize}`;

    this.setState({ loading: true });

    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  handlePrevClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=${
      this.props.country
    }&category=${
      this.props.category
    }&apiKey=4164cc6faad2423fa1df78508369a629&page=${
      this.state.page - 1
    }&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles,
      loading: false,
    });
  };

  handleNextClick = async () => {
    if (
      !(
        this.state.page + 1 >
        Math.ceil(this.state.totalResults / this.props.pageSize)
      )
    ) {
      let url = `https://newsapi.org/v2/top-headlines?country=${
        this.props.country
      }&category=${
        this.props.category
      }&apiKey=4164cc6faad2423fa1df78508369a629&page=${
        this.state.page + 1
      }&pageSize=${this.props.pageSize}`;
      this.setState({ loading: true });
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles,
        loading: false,
      });
    }
  };

  render() {
    return (
      <>
        <div className='container my-3'>
          <h1 className='text-center my-4'>
            News App - Top Headlines on
            {capitalFirstLetter(this.props.category)}
          </h1>
          {this.state.loading && <Spinner />}
          <div className='row'>
            {!this.state.loading &&
              this.state.articles.map((myElement) => {
                return (
                  <div className='col-md-4' key={myElement.url}>
                    <NewsItem
                      title={
                        myElement.title ? myElement.title.slice(0, 45) : ''
                      }
                      description={
                        myElement.description
                          ? myElement.description.slice(0, 88)
                          : ''
                      }
                      imageUrl={
                        myElement.urlToImage
                          ? myElement.urlToImage
                          : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                      }
                      newsUrl={myElement.url}
                      author={myElement.author ? myElement.author : 'Unknown'}
                      articlesDate={myElement.publishedAt}
                      source={myElement.source.name}
                    />
                  </div>
                );
              })}
          </div>
        </div>
        {!this.state.loading && (
          <div className='container d-flex justify-content-between'>
            <button
              disabled={this.state.page <= 1}
              type='button'
              className='btn btn-dark'
              onClick={this.handlePrevClick}>
              &larr; PREVIOUS
            </button>
            <button
              disabled={
                this.state.page + 1 >
                Math.ceil(this.state.totalResults / this.props.pageSize)
              }
              type='button'
              className='btn btn-dark'
              onClick={this.handleNextClick}>
              NEXT &rarr;
            </button>
          </div>
        )}
      </>
    );
  }
}

export default News;
