import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 8,
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0,
    };

    const capitalFirstLetter = (string) => {
      return (
        string.slice(0, 1).toUpperCase() +
        string.slice(1, string.length).toLowerCase()
      );
    };

    document.title = `${capitalFirstLetter(this.props.category)} - NewsApp`;
  }

  async componentDidMount() {
    this.updateNews();
  }

  updateNews = async () => {
    this.props.setProgress(0);
    this.props.setProgress(10);

    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=4164cc6faad2423fa1df78508369a629&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.props.setProgress(20);
    this.props.setProgress(30);

    this.setState({ loading: true });
    this.props.setProgress(40);
    this.props.setProgress(50);

    let data = await fetch(url);
    this.props.setProgress(60);
    this.props.setProgress(70);

    let parsedData = await data.json();
    this.props.setProgress(80);
    this.props.setProgress(90);

    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
    this.props.setProgress(100);
  };

  // handlePrevClick = async () => {
