  //     this.props.country
  //   }&category=${
  //     this.props.category
  //   }&apiKey=4164cc6faad2423fa1df78508369a629&page=${
  //     this.state.page - 1
  //   }&pageSize=${this.props.pageSize}`;
  //   this.setState({ loading: true });
  //   let data = await fetch(url);
  //   let parsedData = await data.json();
  //   this.setState({
  //     page: this.state.page - 1,
  //     articles: parsedData.articles,
  //     loading: false,
  //   });
  // };

  // handleNextClick = async () => {
  //   if (
  //     !(
  //       this.state.page + 1 >
  //       Math.ceil(this.state.totalResults / this.props.pageSize)
  //     )
  //   ) {
  //     let url = `https://newsapi.org/v2/top-headlines?country=${
  //       this.props.country
  //     }&category=${
  //       this.props.category
  //     }&apiKey=4164cc6faad2423fa1df78508369a629&page=${
  //       this.state.page + 1
  //     }&pageSize=${this.props.pageSize}`;
  //     this.setState({ loading: true });
  //     let data = await fetch(url);
  //     let parsedData = await data.json();
  //     console.log(parsedData);
  //     this.setState({
  //       page: this.state.page + 1,
  //       articles: parsedData.articles,
  //       loading: false,
  //     });
  //   }
  // };

  fetchMoreData = async () => {
    this.setState({ page: this.state.page + 1 });
    this.updateNews();
  };

  render() {
    return (
      <>
        <h1 className='text-center my-4'>
          NEWSAPP TOP {this.props.category.toUpperCase()} HEADLINES
        </h1>
        {/* {this.state.loading && <Spinner />} */}
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalResults}
          loader={<Spinner />}>
          <div className='container'>
            <div className='row'>
              {this.state.articles.map((myElement) => {
                return (
                  <div className='col-md-4' key={myElement.url}>
                    <NewsItem
                      title={myElement.title}
                      description={
                        myElement.description ? myElement.description : ''
                      }
                      imageUrl={
                        myElement.urlToImage
                          ? myElement.urlToImage
                          : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                      }
                      newsUrl={myElement.url}
                      author={myElement.author ? myElement.author : 'Unknown'}
                      articlesDate={myElement.publishedAt}
                      source={myElement.source.name}
                    />
                  </div>
                );
              })}
            </div>
          </div>
        </InfiniteScroll>
      </>
    );
  }
}

export default News;
