import React, { Component } from 'react';
import NewsItem from './NewsItem';

export class News extends Component {
  articles = [
    {
      source: { id: 'bbc-sport', name: 'BBC Sport' },
      author: 'BBC Sport',
      title: 'Ex-India off-spinner Harbhajan retires',
      description:
        'Former India off-spinner Harbhajan Singh retires from all forms of cricket, making the announcement in a social media post.',
      url: 'http://www.bbc.co.uk/sport/cricket/59781100',
      urlToImage:
        'https://ichef.bbci.co.uk/live-experience/cps/624/cpsprodpb/10D5F/production/_122495986_gettyimages-507138108.jpg',
      publishedAt: '2021-12-24T11:07:26.491929Z',
      content:
        'Harbhajan Singh (left) watched on from the dugout and changing room as the delayed Indian Premier League was completed in the United Arab Emirates in October\r\nFormer India off-spinner Harbhajan Singh… [+2009 chars]',
    },
    {
      source: { id: 'espn-cric-info', name: 'ESPN Cric Info' },
      author: null,
      title:
        'PCB hands Umar Akmal three-year ban from all cricket | ESPNcricinfo.com',
      description:
        'Penalty after the batsman pleaded guilty to not reporting corrupt approaches | ESPNcricinfo.com',
      url: 'http://www.espncricinfo.com/story/_/id/29103103/pcb-hands-umar-akmal-three-year-ban-all-cricket',
      urlToImage:
        'https://a4.espncdn.com/combiner/i?img=%2Fi%2Fcricket%2Fcricinfo%2F1099495_800x450.jpg',
      publishedAt: '2020-04-27T11:41:47Z',
      content:
        "Umar Akmal's troubled cricket career has hit its biggest roadblock yet, with the PCB handing him a ban from all representative cricket for three years after he pleaded guilty of failing to report det… [+1506 chars]",
    },
    {
      source: { id: 'espn-cric-info', name: 'ESPN Cric Info' },
      author: null,
      title:
        'What we learned from watching the 1992 World Cup final in full again | ESPNcricinfo.com',
      description:
        'Wides, lbw calls, swing - plenty of things were different in white-ball cricket back then | ESPNcricinfo.com',
      url: 'http://www.espncricinfo.com/story/_/id/28970907/learned-watching-1992-world-cup-final-full-again',
      urlToImage:
        'https://a4.espncdn.com/combiner/i?img=%2Fi%2Fcricket%2Fcricinfo%2F1219926_1296x729.jpg',
      publishedAt: '2020-03-30T15:26:05Z',
      content:
        'Last week, we at ESPNcricinfo did something we have been thinking of doing for eight years now: pretend-live ball-by-ball commentary for a classic cricket match. We knew the result, yes, but we tried… [+6823 chars]',
    },
  ];

  constructor() {
    super();
    this.state = {
      articles: this.articles,
      loading: false,
    };
  }

  async componentDidMount() {
    let url =
      'https://newsapi.org/v2/top-headlines?country=in&apiKey=4164cc6faad2423fa1df78508369a629';
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }
  render() {
    return (
      <>
        <div className='container my-3'>
          <h2 className='text-center my-4'>News Monkey - Top Headlines</h2>

          <div className='row'>
            {this.state.articles.map((myElement) => {
              return (
                <div className='col-md-4' key={myElement.url}>
                  <NewsItem
                    title={myElement.title ? myElement.title.slice(0, 45) : ''}
                    description={
                      myElement.description
                        ? myElement.description.slice(0, 88)
                        : ''
                    }
                    imageUrl={
                      myElement.urlToImage
                        ? myElement.urlToImage
                        : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                    }
                    newsUrl={myElement.url}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

export default News;
