import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }

  async componentDidMount() {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=4164cc6faad2423fa1df78508369a629&page=1&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });

    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  handlePrevClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=4164cc6faad2423fa1df78508369a629&page=${
      this.state.page - 1
    }&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles,
      loading: false,
    });
  };

  handleNextClick = async () => {
    if (
      !(
        this.state.page + 1 >
        Math.ceil(this.state.totalResults / this.props.pageSize)
      )
    ) {
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=4164cc6faad2423fa1df78508369a629&page=${
        this.state.page + 1
      }&pageSize=${this.props.pageSize}`;
      this.setState({ loading: true });
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles,
        loading: true,
      });
    }
  };

  render() {
    return (
      <>
        <div className='container my-3'>
          <h1 className='text-center my-4'>News Monkey - Top Headlines</h1>
          {this.state.loading && <Spinner />}
          <div className='row'>
            {this.state.articles.map((myElement) => {
              return (
                <div className='col-md-4' key={myElement.url}>
                  <NewsItem
                    title={myElement.title ? myElement.title.slice(0, 45) : ''}
                    description={
                      myElement.description
                        ? myElement.description.slice(0, 88)
                        : ''
                    }
                    imageUrl={
                      myElement.urlToImage
                        ? myElement.urlToImage
                        : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                    }
                    newsUrl={myElement.url}
                  />
                </div>
              );
            })}
          </div>
        </div>
        <div className='container d-flex justify-content-between'>
          <button
            disabled={this.state.page <= 1}
            type='button'
            className='btn btn-dark'
            onClick={this.handlePrevClick}>
            &larr; PREVIOUS
          </button>
          <button
            disabled={
              this.state.page + 1 >
              Math.ceil(this.state.totalResults / this.props.pageSize)
            }
            type='button'
            className='btn btn-dark'
            onClick={this.handleNextClick}>
            NEXT &rarr;
          </button>
        </div>
      </>
    );
  }
}

export default News;
