import React, { useEffect } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  // constructor(props) {
  //     super(props);

  //       const capitalFirstLetter = (string) => {
  //       return (
  //         string.slice(0, 1).toUpperCase() +
  //         string.slice(1, string.length).toLowerCase()
  //       );
  //     };

  //     document.title = `${capitalFirstLetter(props.category)} - NewsApp`;
  //   }

  const updateNews = async () => {
    props.setProgress(0);
    props.setProgress(10);

    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apikey}&page=${this.state.page}&pageSize=${props.pageSize}`;
    props.setProgress(20);
    props.setProgress(30);

    this.setState({ loading: true });
    props.setProgress(40);
    props.setProgress(50);

    let data = await fetch(url);
    props.setProgress(60);
    props.setProgress(70);

    let parsedData = await data.json();
    props.setProgress(80);
    props.setProgress(90);

    setArticles(parsedData.articles);
    setTotalResults(parsedData.totalResults);
    setLoading(false);

    props.setProgress(100);
  };

  useEffect(() => {
    updateNews();
  }, []);

  const handlePrevClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=${
      props.country
    }&category=${props.category}&apiKey=4164cc6faad2423fa1df78508369a629&page=${
      this.state.page - 1
    }&pageSize=${props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles,
      loading: false,
    });
  };

  const handleNextClick = async () => {
    if (
      !(
        this.state.page + 1 >
        Math.ceil(this.state.totalResults / props.pageSize)
      )
    ) {
      let url = `https://newsapi.org/v2/top-headlines?country=${
        props.country
      }&category=${
        props.category
      }&apiKey=4164cc6faad2423fa1df78508369a629&page=${
        this.state.page + 1
      }&pageSize=${props.pageSize}`;
      this.setState({ loading: true });
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles,
        loading: false,
      });
    }
  };

  fetchMoreData = async () => {
    this.setState({ page: this.state.page + 1 });
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apikey}&page=${this.state.page}&pageSize=${props.pageSize}`;

    this.setState({ loading: true });

    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: this.state.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false,
    });
  };

  return (
    <>
      <h1 className='text-center my-4'>
        NEWSAPP TOP {props.category.toUpperCase()} HEADLINES
      </h1>
      {/* {this.state.loading && <Spinner />} */}
      <InfiniteScroll
        dataLength={this.state.articles.length}
        next={this.fetchMoreData}
        hasMore={this.state.articles.length !== this.state.totalResults}
        loader={<Spinner />}>
        <div className='container'>
          <div className='row'>
            {this.state.articles.map((myElement) => {
              return (
                <div className='col-md-4' key={myElement.url}>
                  <NewsItem
                    title={myElement.title}
                    description={
                      myElement.description ? myElement.description : ''
                    }
                    imageUrl={
                      myElement.urlToImage
                        ? myElement.urlToImage
                        : 'https://ichef.bbci.co.uk/news/1024/branded_news/6D88/production/_122504082_mediaitem122504081.jpg'
                    }
                    newsUrl={myElement.url}
                    author={myElement.author ? myElement.author : 'Unknown'}
                    articlesDate={myElement.publishedAt}
                    source={myElement.source.name}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </InfiniteScroll>
    </>
  );
};

News.defaultProps = {
  country: 'in',
  pageSize: 8,
};

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
};

export default News;
